import React, { useState, useEffect } from 'react';

export default function AddAmount(props) {
  const [amountToBeAdded, addAmount] = useState(0);
  const [trasctionType, setTransactionType] = useState('');

  var handleButtonclick = () => {
    props.handleAmountAddition(amountToBeAdded, trasctionType);
  }

 var handleValueChange = (e) => {
  addAmount(e.target.value);
 }

 var handletransactionType = (e) => {
  setTransactionType(e.target.value);
 }
  return (
    <>
      <div>
      <p>Please select the Action:</p>
      <input type="radio" id="add" name="transactionType" value="add" onChange={handletransactionType} />
      <label htmlFor="add">Add amount </label>

      <input type="radio" id="withdraw" name="transactionType" value="withdraw" onChange={handletransactionType} />
      <label htmlFor="withdraw">Withdraw amount </label>
      </div>
      <div>

      </div>
      <input type="number" onChange={handleValueChange} />
      <button onClick={handleButtonclick}> Submit </button>
    </>
  );
}
