import React, { useState, useEffect } from 'react';

export default function AddAmount(props) {
  [amountToBeAdded, addAmount] = useState(0);


  var handleButtonclick = () => {
    props.handleAmountAddition(amountToBeAdded);
  }

 var handleValueChange = (e) => {
  addAmount(e.target.value);
 }

  return (
    <>
      <p>Please select the Action:</p>
      <input type="radio" id="add" name="transactionType" value="add" onChange={handleValueChange} />
      <label for="add">Add amount: </label>
      <input type="radio" id="withdraw" name="transactionType" value="withdraw" onChange={handleValueChange} />
      <label for="withdraw">Withdraw amount: </label>
      <input type="number" onChange={handleValueChange} />
      <button onClick={handleButtonclick}>Add Amount</button>
    </>
  );
}
