import React, { useState, useEffect } from 'react';

export default function AddAmount(props) {
  [amountToBeAdded, addAmount] = useState(0);

  var handleButtonclick = () => {
    props.handleAmountAddition();
  }

  return (
    <>
      <div>Debit Card</div>
      <label for="addAmount">Amount to be added: </label>
      <input id="addAmount" type="number" name="amount" value={amountToBeAdded}/>
      <button onClick={handleButtonclick}>Add Amount</button>
    </>
  );
}
